def identify_type(value):
    if type(value) == int:
        return "integer"
    elif type(value) == str:
        return "string"
    elif type(value) == float:
        return "float"
    elif type(value) == bool:
        return "boolean"
    else:
        return "unknown type"

# Test
print(identify_type(42))        # "integer"
print(identify_type("42"))      # "string"
print(identify_type(42.0))      # "float"
print(identify_type(True))      # "boolean"
