def identify_type(value):
    if type(value) == int:
        return "integer"
    elif type(value) == str:
        return "string"
    elif type(value) == float:
        return "float"
    elif type(value) == bool:
        return "boolean"
    else:
        return "unknown type"

# Test
print(identify_type(42))        # "integer"
print(identify_type("42"))      # "string"
print(identify_type(42.0))      # "float"
print(identify_type(True))      # "boolean"

def smart_convert(value):
    # Try integer conversion first
    try:
        return int(value)
    except ValueError:
        # Try float conversion
        try:
            return float(value)
        except ValueError:
            # Return original string
            return value

# Test cases
print(smart_convert("42"))      # 42 (int)
print(smart_convert("3.14"))    # 3.14 (float)
print(smart_convert("hello"))   # "hello" (str)
print(smart_convert("123.0"))   # 123.0 (float)

def process_mixed_list(mixed_list):
    numeric_list = []

    for item in mixed_list:
        # If already a number, add it
        if isinstance(item, (int, float)):
            numeric_list.append(item)
        # If string, try to convert
        elif isinstance(item, str):
            try:
                # Try int first
                numeric_list.append(int(item))
            except ValueError:
                try:
                    # Try float
                    numeric_list.append(float(item))
                except ValueError:
                    # Skip non-numeric strings
                    pass

    # Sort and return
    numeric_list.sort()
    return numeric_list

# Test cases
test1 = ["5", 2, "hello", "3.14", 10, "world", "7"]
print(process_mixed_list(test1))  # [2, 3.14, 5, 7, 10]

test2 = ["42", "abc", "0", "-5", "3.5"]
print(process_mixed_list(test2))  # [-5, 0, 3.5, 42]

def calculate(a, b, operation):
    if operation == 'add':
        return a + b
    elif operation == 'subtract':
        return a - b
    elif operation == 'multiply':
        return a * b
    elif operation == 'divide':
        if b != 0:
            return a / b
        else:
            return "Error: Division by zero"
    elif operation == 'floor_divide':
        if b != 0:
            return a // b
        else:
            return "Error: Division by zero"
    elif operation == 'modulo':
        if b != 0:
            return a % b
        else:
            return "Error: Division by zero"
    elif operation == 'power':
        return a ** b
    else:
        return "Error: Unknown operation"

# Test cases
print(calculate(10, 3, 'add'))          # 13
print(calculate(10, 3, 'subtract'))     # 7
print(calculate(10, 3, 'multiply'))     # 30
print(calculate(10, 3, 'divide'))       # 3.333...
print(calculate(10, 3, 'floor_divide')) # 3
print(calculate(10, 3, 'modulo'))       # 1
print(calculate(2, 8, 'power'))         # 256
